<script setup>
import Input from '../components/Input.vue'
import Loader from '../components/Loader.vue'
import Button from '../components/Button.vue'
import state from './state';

</script>

<template>
  <div class="payment-page">
    <div class="invoice-info" v-if="!this.loader">
      <div class="title">
        <span>Призначення платежа:</span> "{{ this.description }}"
      </div>
      <div class="amount">
      <span>Сума до сплати:</span> {{ this.amount }} грн
      </div>
    </div>
    <Loader v-if="this.loader"/>
  </div>
  <div class="form-to-pay">
    <div v-if="!this.loader && !this.lookup && !this.success && !this.failed">
      <div class="card-number">
        <Input v-model="cardPan" placeholder="0000 0000 0000 0000" label="Номер карти" typeOfInput="text" :style="{ width: '388px' }"></Input>
      </div>
      <div class="cardSec">
        <Input placeholder="MM-YY" label="Термін дії" typeOfInput="text" :style="{ width: '180px' }"></Input>
        <Input placeholder="***" label="CVV" typeOfInput="password" :style="{ width: '180px' }" v-model="this.cvv"></Input>
      </div>
      <div class="policy">Натискаючи на кнопку "Оплати", ви підтверджуєте що ознайомлені з переліком інформаціх про послугу та приймаєте умови <a href="">публічного договору</a></div>
      <Button class="but" @click="submit" value="Сплатити"></Button>
    </div>
    <Loader v-if="this.loader"/>
    <div class="lookup" v-if="this.lookup">
      <Input placeholder="000000" label="Введіть код з СМС" typeOfInput="text" :style="{ width: '180px' }"></Input>
      <Button class="but" @click="submitLookup" value="Підтвердити"></Button>
    </div>
    <div class="success" v-if="this.success">
      <div class="success-text">Платіж сплачено успішно</div>
      <img src="data:image/png;base64,"/>
      <Button class="but" @click="downloadReceipt" value="Отримати квитанцію"></Button>
    </div>
    <div class="success" v-if="this.failed">
      <div class="success-text">Щось пішло не так</div>
      <img src="data:image/png;base64,"/>
      <!-- <button class="but" @click="submitLookup">Отримати квитанцію</button> -->
    </div>
  </div>
</template>

<script>
export default {
  mounted() {
    const invoice = JSON.parse(localStorage.getItem('invoice'));
    this.description = invoice.description;
    this.amount = invoice.amount;
    this.iban = invoice.iban;
    this.serviceDestination = invoice.serviceDestination;
    localStorage.removeItem('invoice');
  },
  data() {
    return {
      description: '',
      amount: '',
      loader: false,
      lookup: false,
      success: false,
      failed: false,
      iban: '',
      serviceDestination: '',
      cvv: '',
      cardPan: '',
    };
  },
  methods: {
    submit() {
      this.loader = true;
      setTimeout(() => { 
        this.lookup = true;
        this.loader = false;
      }, 3000)
    },
    submitLookup() {
      this.lookup = false;
      this.loader = true;
      setTimeout(() => {
        this.loader = false;
        if(this.cvv === '123') {
          this.success = true;
          const payments = localStorage.getItem('payments');
          if(!payments) {
            localStorage.setItem('payments', JSON.stringify([{
              date: new Date().toISOString(),
              description: this.description,
              amount: this.amount,
              cardMasked: `${this.cardPan.slice(0, 6)}******${this.cardPan.slice(12)}`,
              iban: this.iban,
              serviceDestination: this.serviceDestination,
              status: 'FINISHED'
            }]))
            return;
          }
          const parsed = JSON.parse(payments);
          parsed.push({
            date: new Date().toISOString(),
            description: this.description,
            amount: this.amount,
            iban: this.iban,
              serviceDestination: this.serviceDestination,
            cardMasked: `${this.cardPan.slice(0, 6)}******${this.cardPan.slice(12)}`,
            status: 'FINISHED'
          })
          localStorage.setItem('payments', JSON.stringify(parsed));
        }
        if(this.cvv === '124') {
          this.failed = true;
          const payments = localStorage.getItem('payments');
          if(!payments) {
            localStorage.setItem('payments', JSON.stringify([{
              date: new Date(),
              description: this.description,
              amount: this.amount,
              iban: this.iban,
              serviceDestination: this.serviceDestination,
              cardMasked: `${this.cardPan.slice(0, 6)}******${this.cardPan.slice(12)}`,
              status: 'REJECTED'
            }]))
            return;
          }
          const parsed = JSON.parse(payments);
          parsed.push({
            date: new Date(),
            description: this.description,
            amount: this.amount,
            iban: this.iban,
              serviceDestination: this.serviceDestination,
            cardMasked: `${this.cardPan.slice(0, 6)}******${this.cardPan.slice(12)}`,
            status: 'REJECTED'
          })
          localStorage.setItem('payments', JSON.stringify(parsed));
        }
      }, 3000);
    },
    downloadReceipt() {
      const url = 'data:application/pdf;base64,'
      const link = document.createElement('a')
      link.href = url
      link.download = 'Квитанція'
      link.click()
      URL.revokeObjectURL(link.href)
    }
  }
}
</script>

